describe("radio.channel",function(){var e,t;describe("with no arguments",function(){it("should throw an exception",function(){expect(function(){Wreqr.radio.channel()}).toThrow()})}),describe("for a nonexistent channel",function(){beforeEach(function(){e=Wreqr.radio.channel("lala")}),it("should return an instance of the default channel",function(){expect(e.channelName).toEqual("lala")})}),describe("twice with the same name",function(){var e,t;beforeEach(function(){e=Wreqr.radio.channel("lala"),t=Wreqr.radio.channel("lala")}),it("should return the same channel",function(){expect(e).toBe(t)})})});